import {useEffect, useState} from 'react'
import {Colors} from './Text'
import './f.css'
export default function App() {
  const [color,setColor] = useState(Colors[0].two)
  const [o,setO] = useState(Colors[0]);

const add = (e) => {
e.appedChild = <span></span>
}


const [te,setTe] = useState(
    <>
<div className="do" style={{
  backgroundColor:o.one,
}} onClick={(e)=>add()}>
{o.one}
</div>
<div className="do" style={{
  backgroundColor:o.two,
}}  onClick={(e)=>add()}>
{o.two}
</div>
<div className="do" style={{
  backgroundColor:o.three,
}}  onClick={(e)=>add()}>
{o.three}
</div>
</>
  )
useEffect(()=>
{  setTe(
  <>
  <div className="do" style={{
    backgroundColor:o.one,
  }}>
  {o.one}
  </div>
  <div className="do" style={{
    backgroundColor:o.two,
  }}>
  {o.two}
  </div>
  <div className="do" style={{
    backgroundColor:o.three,
  }}>
  {o.three}
  </div>
  </>

  )
}  
,[o])

const ok = () => {
let op = Colors.filter(x=> x.two == color)
setO(op[0])
}

  return (
    <div className="container">
      <h1>Our Coloers</h1> 
    <input type='text' onChange={(e)=>setColor(e.target.value)} className='btn bg-info p-3 my-5'/>
      <button onClick={()=> ok()} 
      className='btn bg-success p-3 m-5'>Click</button>


<div className="d-flex">
<div className="do" style={{
  border:'.3px solid black',

  backgroundColor:'white',
  color:'black'
}}>
#fff
</div>

{te}

<div className="do" style={{
  backgroundColor:'black',
}}>
#000
</div>
</div>

    </div>
  )
}
