import { BreakFast,Lunch,Dinner } from "./Text";
import {useState} from 'react'
import MiniMap from "./MiniMap";
export default function App() {
  const [current,setCurrent] = useState([])
  console.log(current)
  if(current.length == 0){
    return ( <>{console.log("ok")} </>

 )
  }else{
     return (
    <div className="container">
      <div>
     <h1 className="bg-warning p-5 rounded text-center mt-3">Our Contacts</h1>
     <div className="text-center">
     <button onClick={()=>setCurrent(BreakFast)} className="btn bg-danger p-2 mx-3">Breakfast</button>
     <button onClick={()=>setCurrent(Lunch)} className="btn bg-danger p-2 mx-3">Lunch</button>
     <button onClick={()=>setCurrent(Dinner)} className="btn bg-danger p-2 mx-3">Dinner</button>
     </div>

      </div>
    {current.map(x => <MiniMap key={x.id} {...x}/>)}
    </div>
  );

  }

}
